*/

//*********************************************************

    handleInputChanged = (e) => {
        this.setState({[ e.target.name]: e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.history.push('/predict') //redirects to predict route

        //const description = this.state;

        const search = {
            description
        };

/*        axios
            .post('', search)
            .then(() => console.log('search made'))
            .catch(err => {
