        console.log('file uploaded');

        //axios.post('', fileData);



    }
*/

//*********************************************************

    handleInputChanged = (e) => {
        this.setState({[ e.target.name]: e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.history.push('/predict') //redirects to predict route


/*        axios
            .post('', search)
            .then(() => console.log('search made'))
            .catch(err => {
                console.log(err);
            })*/

    };


    render() {
        return (
            <div className={'page-container'}>
                <div className={"form-container"}>
                        <h1 className="heading">Input Data</h1>
                    <div className={"container"}>
                            <form onSubmit={this.handleSubmit}>
                                {/*make into a text box, make button below*/}
                                {/*<input className={"file-upload"} type={"file"} name={"file"}/>*/}
                                <input type={"text"} name={"gender"} placeholder={"gender"} onChange={this.handleInputChanged}/>
                                <input type={"text"} name={"age"} placeholder={"age"} onChange={this.handleInputChanged}/>
                                <input type={"text"} name={"size"} placeholder={"size"} onChange={this.handleInputChanged}/>
                                <input type={"text"} name={"image-url"} placeholder={"image url"} onChange={this.handleInputChanged}/>
                                <button type="submit" id="search-button" className="btn btn-primary">
                                    <i>Search</i>
                                </button>
                            </form>
                    </div>
                </div>
            </div>
        )
    }

}
